import jsonimport osCONFIG_FILE = "config.json"def load_config():    default_config = {        "ADMIN_ID": "",        "BOT_TOKEN": "",        "BOT_PASSWORD": "",        "ADMINS": []    }    if not os.path.exists(CONFIG_FILE):        with open(CONFIG_FILE, "w", encoding="utf-8") as f:            json.dump(default_config, f, ensure_ascii=False, indent=2)        config = default_config    else:        with open(CONFIG_FILE, "r", encoding="utf-8") as f:            config = json.load(f)    # BOT_TOKEN من ENV إذا موجود    env_token = os.getenv("BOT_TOKEN")    if env_token:        config["BOT_TOKEN"] = env_token    # تصحيح ADMIN_ID    try:        config["ADMIN_ID"] = int(config["ADMIN_ID"])    except:        config["ADMIN_ID"] = ""    # تصحيح ADMINS    config["ADMINS"] = [        int(admin) for admin in config.get("ADMINS", [])    ]    return configdef save_config(data):    with open(CONFIG_FILE, "w", encoding="utf-8") as f:        json.dump(data, f, ensure_ascii=False, indent=2)def is_admin(user_id):    config = load_config()    return (        user_id == config.get("ADMIN_ID")        or user_id in config.get("ADMINS", [])    )